  name: 'page:Home'
  view: __dirname
  style: __dirname
module.exports = class CreateGame
  name: 'CreateGame'
  view: __dirname
  style: __dirname

  createGame: ->
    gameName = @model.del 'gameName'
    unless gameName? and gameName.trim().length > 0
      return alert 'You need to set the name of the game'
    @model.root.add 'games',
      name: gameName
      userIds: []
      rounds: [1..8]
      maxPlayers: 2
      currentRound: 1
      started: false
      finished: false
module.exports = class GameInfo
  name: 'GameInfo'
  view: __dirname
  style: __dirname

  init: ->
    @model.ref 'game', @model.scope '_page.game'
    @model.ref 'players', @model.scope '_page.players'
reactiveFns = require '../Libs/reactiveFns'

module.exports = class GamesList
  name: 'GamesList'
  view: __dirname
  style: __dirname

  init: ->
    @model.ref 'games', @model.scope '_page.games'
    @model.ref 'user', @model.scope '_page.user'
    @userId = @model.scope().get '_session.userId'
    @model.start 'gamesList', 'games', 'user', reactiveFns.getGamesList

  join: (game, e) ->
    e.preventDefault()
    isProfessor = @model.get 'user.isProfessor'
    userName = @model.get 'user.userName'
    unless userName? and userName.trim().length > 0
      return alert 'You do not have a name'
    if not(@userId in game.userIds)
      if not isProfessor
        @model.root.add 'players',
          gameId: game.id
          playerId: @userId
          quantities: []
      @model.root.push 'games.' + game.id + '.userIds', @userId
      @app.history.push '/game/' + game.id
_ = require 'lodash'

exports.getGamesList = (games, user) ->
  list = []
  for game in games
    object =
      name: game.name
      id: game.id
      userIds: game.userIds
      canJoin: !!(game.userIds.length < game.maxPlayers || user.isProfessor)
    list.push object
  list

exports.calculatePrices = (players, currentRound) ->
  prices = []
  if currentRound > 1
    for i in [0..currentRound - 2]
      sum = 0
      for player in players
        sum += parseFloat(player.quantities[i]) if player.quantities[i]?
      prices[i] = 45 - 0.2 * sum
  prices

exports.calculateProfits = (players, prices, currentRound) ->
  costPerShrimp = 5
  profits = {}
  for player in players
    profits[player.playerId] = []
    for i in [0..currentRound - 1]
      if player.quantities[i]? and prices[i]?
        profits[player.playerId].push player.quantities[i] * (prices[i] - costPerShrimp)
  profits

exports.calculateTotalProfits = (players, profits, currentRound) ->
  totalProfits = {}
  if currentRound > 1
    for player in players
      totalProfit = 0
      for i in [0..currentRound - 2]
        totalProfit += profits[player.playerId][i] if profits[player.playerId]?[i]?
      totalProfits[player.playerId] = totalProfit
  totalProfits

exports.getTheWinner = (totalProfits, players, isFinished) ->
  if isFinished
    for player in players
      if totalProfits[player.playerId] is _.max(totalProfits)
        winnerId = player.playerId
        break
  winnerId
module.exports = class Professor
  name: 'Professor'
  view: __dirname
  style: __dirname

  init: ->
    @model.ref 'game', @model.scope '_page.game'
    @model.ref 'players', @model.scope '_page.players'

  goToTheNextRound: ->
    players = @model.get 'players'
    currentRound = @model.get 'game.currentRound'
    for player, i in players
      @model.set "players.#{i}.quantities.#{currentRound - 1}", 0 unless player.quantities[currentRound - 1]?
    @model.increment 'game.currentRound'
consts = require '../../apps/consts/consts.json'

module.exports = class SubmitForm
  name: 'SubmitForm'
  view: __dirname
  style: __dirname

  init: ->
    @model.ref 'game', @model.scope '_page.game'
    @userId = @model.scope().get '_session.userId'
    @model.ref 'players', @model.scope '_page.players'

  setQuantity: ->
    quantity = @model.del 'quantity'
    currentRound = @model.get 'game.currentRound'
    userIds = @model.get 'game.userIds'
    maxPlayers = @model.get 'game.maxPlayers'
    rounds = @model.get 'game.rounds'
    if isNaN(parseFloat(quantity)) or not(consts.MIN_QUANTITY < parseFloat(quantity) <= consts.MAX_QUANTITY)
      return alert 'Wrong input!(should be a number between 0 and 75)'
    count = 0
    index = 0
    for id, i in @model.get 'game.userIds'
      if id is @userId
        index = i
        @model.set "players.#{index}.quantities.#{currentRound - 1}", quantity
        break
    for player in @model.get 'players'
      unless player.quantities[currentRound - 1]
        break
      count++
    if count is parseFloat(maxPlayers)
      @model.increment 'game.currentRound'
    if @model.get('game.currentRound') > rounds.length
      @model.set 'game.finished', true
_ = require 'lodash'
reactiveFns = require '../Libs/reactiveFns'

module.exports = class Table
  name: 'Table'
  view: __dirname
  style: __dirname

  init: ->
    @model.ref 'game', @model.scope '_page.game'
    @model.ref 'players', @model.scope '_page.players'
    @model.ref 'currentRound', @model.scope '_page.game.currentRound'
